mapper.update:
update tbl_roles
set role_name = #{roleName,jdbcType=VARCHAR},
  state = #{state,jdbcType=TINYINT},
  permissions_str = #{permissionsStr,jdbcType=VARCHAR}
where role_id = #{roleId,jdbcType=INTEGER}

mapper.select:
select role_id, role_name, state, permissions_str
from tbl_roles
where role_id = #{roleId,jdbcType=INTEGER}
